package test

import (
	"bytes"
	"encoding/base64"
	"encoding/json"
	"github.com/stretchr/testify/assert"
	"imageResample/internal/config"
	"imageResample/internal/service"
	"imageResample/internal/storage"
	adapter "imageResample/internal/transport/http"
	"imageResample/pkg/api"
	"log/slog"
	"net/http"
	"net/http/httptest"
	"os"
	"testing"
)

const (
	image100x100png = ""
	image100x100jpg = ""
)

func TestResampler(t *testing.T) {
	//  
	cfg := &config.Config{
		ImageWidth:  50,
		ImageHeight: 50,
	}
	mockStorage := storage.NewMockStorage() //  
	log := slog.New(slog.NewTextHandler(os.Stdout, &slog.HandlerOptions{}))
	resampler := service.NewResampler(mockStorage, cfg.ImageWidth, cfg.ImageHeight, log)
	handler := adapter.NewResizeHandler(resampler, log)

	t.Run("Valid image upload", func(t *testing.T) {

		request := api.ImageRequest{Image: image100x100jpg}
		reqBody, err := json.Marshal(request)
		assert.NoError(t, err)

		req := httptest.NewRequest(http.MethodPost, "/", bytes.NewReader(reqBody))
		rec := httptest.NewRecorder()

		handler.ServeHTTP(rec, req)

		resp := rec.Result()
		assert.Equal(t, http.StatusOK, resp.StatusCode)

		var responseBody map[string]interface{}
		err = json.NewDecoder(resp.Body).Decode(&responseBody)
		assert.NoError(t, err)
		assert.Contains(t, responseBody, "time")
		assert.Contains(t, responseBody, "cached")
	})

	t.Run("Invalid image format", func(t *testing.T) {

		request := api.ImageRequest{Image: image100x100png}
		reqBody, err := json.Marshal(request)
		assert.NoError(t, err)

		req := httptest.NewRequest(http.MethodPost, "/", bytes.NewReader(reqBody))
		rec := httptest.NewRecorder()

		handler.ServeHTTP(rec, req)

		resp := rec.Result()
		assert.Equal(t, http.StatusBadRequest, resp.StatusCode)

		var responseBody map[string]interface{}
		err = json.NewDecoder(resp.Body).Decode(&responseBody)
		assert.NoError(t, err)
		assert.Contains(t, responseBody, "error")
		assert.Contains(t, "image format is invalid", responseBody["error"])
	})

	t.Run("Invalid image upload", func(t *testing.T) {
		invalidData := "some random binary data that is not a valid image"
		encoded := base64.StdEncoding.EncodeToString([]byte(invalidData))

		request := api.ImageRequest{Image: encoded}
		reqBody, err := json.Marshal(request)
		assert.NoError(t, err)

		req := httptest.NewRequest(http.MethodPost, "/", bytes.NewReader(reqBody))
		rec := httptest.NewRecorder()

		handler.ServeHTTP(rec, req)

		resp := rec.Result()
		assert.Equal(t, http.StatusBadRequest, resp.StatusCode)

		var responseBody map[string]interface{}
		err = json.NewDecoder(resp.Body).Decode(&responseBody)
		assert.NoError(t, err)
		assert.Contains(t, responseBody, "error")
	})
}
